class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long cnt = 0;
        int n = nums.size();
        unordered_map<long long , long long> oldres;
        for(int i = 0 ; i < n ; i++){
            unordered_map<long long , long long> curr;
            if(nums[i] == k) cnt++;
            curr[nums[i]] = 1;
            for(auto& [key , freq]: oldres){
                long long newres = key & nums[i];
                if(newres == k) cnt += freq;
                curr[newres] += freq;
            }

            oldres = curr;
        }

        return cnt;
    }
};
