class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int si=nums.size();
        long long t=0;
        unordered_map<long long, long long>pr;
        for(int i=0;i<si;++i){
            unordered_map<long long, long long>cr;
            if(nums[i]==k){
                ++t;
            }
            cr[nums[i]]=1;
            for(auto& [value,frequency]:pr){
                long long nr= value & nums[i];
                if(nr==k){
                    t+=frequency;
                }
                cr[nr]+=frequency;
            }
            pr=cr;
        }
        return t;
    }
};
