class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        unordered_map<int, int> mp;

        for (int i = 0; i < n; i++) {
            unordered_map<int, int> mp1;
            mp1[nums[i]]++;
            for (auto& p : mp) {
                int k = p.first & nums[i];
                mp1[k] += p.second;
            }
            mp = mp1;
            ans += mp[k];
        }

        return ans;
    }
};
