class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
           long long count = 0;
        unordered_map<int, int> curr;

        for (int i = 0; i < nums.size(); ++i) {
            unordered_map<int, int> temp;

            if (nums[i] == k) {
                count++;
            }
            temp[nums[i]]++;

            for (auto& [AND, freq] : curr) {
                int new_value = AND & nums[i];
                if (new_value == k) {
                    count += freq;
                }
                temp[new_value] += freq;
            }

            curr = temp;
        }

        return count;
    }
};
