class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int d = nums.size();
        long long e = 0;

        unordered_map<long long, long long> f;

        for (int i = 0; i < d; ++i) {
            unordered_map<long long, long long> h;

            if (nums[i] == k) {
                e++;
            }
            h[nums[i]] = 1;

            for (auto& [j, l] : f) {
                long long m = j & nums[i];
                if (m == k) {
                    e += l;
                }
                h[m] += l;
            }

            f = h;
        }
        return e;
    }
};
