class Solution {
public:
    long long countSubarrays(vector<int>& v, int k)
    {
       long long count = 0;
    int n = v.size();
    unordered_map<int, int> freq;

    for (int num : v) {
        unordered_map<int, int> new_freq;
        if (num == k) {
            ++count;
        }
        for (auto [key, value] : freq) {
            int new_key = key & num;
            if (new_key == k) {
                count += value;
            }
            new_freq[new_key] += value;
        }
        new_freq[num]++;
        freq = new_freq;
    }

    return count;
    }
};
