class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int size = nums.size();
        unordered_map<long long, long long> mpp1;
        for (int i = 0; i <= size-1; ++i) {
            if (nums[i] == k) {
                ans = ans + 1;
            }
            unordered_map<long long, long long> mpp2; 
            mpp2[nums[i]] = 1;
            for (auto&[first, second] : mpp1) {
                long long res = first & nums[i];
                if (res == k) {
                    ans = ans + second;
                }
                mpp2[res] += second;
            }
            mpp1 = mpp2;
        }
        return ans;
    }
};
