class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<int, int> dp;

        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> new_dp;
            new_dp[nums[i]]++;
            if (nums[i] == k) {
                count++;
            }
            for (const auto& [and_value, freq] : dp) {
                int new_and_value = and_value & nums[i];
                new_dp[new_and_value] += freq;
                if (new_and_value == k) {
                    count += freq;
                }
            }
        dp = new_dp;
    }

    return count;
    }
};
