class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        dp = defaultdict(int)
        ans = 0
        for num in nums:
            tmp = defaultdict(int)
            tmp[num] = 1
            for x in dp:
                tmp[x & num] += dp[x]
            dp = tmp
            ans += dp[k]
        return ans
