class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0, res = 0;
        unordered_map<long long, long long> mp;
        for(int x: nums) {
            unordered_map<long long, long long> tmp;
            if(x == k) ans++;
            tmp[x] = 1;
            for(auto y : mp) {
                res = y.first & x;
                if(res == k) ans+= y.second;
                tmp[res] += y.second;
            }
            mp = tmp;
        }
        return ans;
    }
};
