class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n = arr.size();
        long long count = 0;
        unordered_map<long long, long long> andFreq;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> newAndFreq;

            newAndFreq[arr[i]]++;
            for (auto& p : andFreq) {
                int newAnd = p.first & arr[i];
                newAndFreq[newAnd] += p.second;
            }

            andFreq = newAndFreq;

            if (andFreq.find(k) != andFreq.end()) {
                count += andFreq[k];
            }
        }

        return count;
    }
};
