class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        int n = nums.size();
        long long totalCount = 0;
        unordered_map<long long, long long> previousResults;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currentResults;

            // Count subarrays ending at index i
            if (nums[i] == target) {
                ++totalCount;
            }
            currentResults[nums[i]] = 1;

            // Update total count using previous results
            for (auto& [prevValue, frequency] : previousResults) {
                long long newAndResult = prevValue & nums[i];
                if (newAndResult == target) {
                    totalCount += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            previousResults = currentResults;
        }

        return totalCount;
    }

};
