class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        and_dict = defaultdict(int)

        for num in nums:
            new_and_dict = defaultdict(int)

            if num == k:
                count += 1

            for value in and_dict:
                new_and = value & num

                if new_and == k:
                    count += and_dict[value]

                new_and_dict[new_and] += and_dict[value]

            new_and_dict[num] += 1
            and_dict = new_and_dict

        return count
