class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        long long cnto = 0;
        int n = nums.size();
        unordered_map<long long, long long> kondli;

        for (int i = 0; i < n; ++i) {

            unordered_map<long long, long long> konsi;


             //dbcichcwi;chwcw kdbcidcbdluicc
            if (nums[i] == k) {
                ++cnto;
            }
//bdbdcbccbcjkbk cdk nsssssssss
            konsi[nums[i]] = 1;

            for (auto& [lo, dedo] : kondli) {
                long long newAndResult = lo & nums[i];
                if (newAndResult == k) {
                    cnto += dedo;
                }
                konsi[newAndResult] += dedo;
            }

            kondli = konsi;
        }

        return cnto;
    }
};
