class Solution {
    public long countSubarrays(int[] nums, int k) {
       int len = nums.length;
        long ans = 0;
        Map<Long, Long> map = new HashMap<>();

        for (int i = 0; i < len; ++i) {
            Map<Long, Long> ct = new HashMap<>();

            if (nums[i] == k) {
                ++ans;
            }

            ct.put((long) nums[i], 1L);

            for (Map.Entry<Long, Long> entry : map.entrySet()) {
                long val = entry.getKey();
                long flag = entry.getValue();

                long sk = val & nums[i];

                if (sk == k) {
                    ans += flag;
                }

                ct.put(sk, ct.getOrDefault(sk, 0L) + flag);
            }

            map = ct;
        }

        return ans;
    }
}
