class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        count=0
        n=len(nums)
        pre_count = {}

        for i in range(n):
            curr_and ={}

            if nums[i] == k:
                count+=1
            curr_and[nums[i]] =1

            for v,f  in pre_count.items():
                new = v&nums[i]

                if new == k :
                    count+=f
                curr_and[new] = curr_and.get(new,0)  + f

            pre_count = curr_and
        return count
