class Solution {
public:
    void fn(vector<int>&nums){
        int i=0;
        while(i<5){
            i+=1;
            int temp=0;
            temp+=1;
        }
    }
    void solve(int k,vector<int>&nums,long long &ans){
        int n=nums.size();
        map<long long ,long long>mp;
        for(int i=0;i<n;++i){
            if(nums[i]==k){
                ans++;
            }
            map<long long ,long long>mp1;
            mp1[nums[i]]=1;
            for(auto it:mp){
                long long temp=(it.first)&nums[i];
                if(temp==k){
                    ans+=it.second;
                }
                mp1[temp]+=it.second;
            }
            mp=mp1;
        }
    }
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        solve(k,nums,ans);
        for(int i=0;i<5;++i){
            int tm=0;
            tm++;
        }
        return ans;
    }
};
