#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<long long, long long> mp;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> mp2;
            if (nums[i] == k) {
                count++;
            }
            mp2[nums[i]]++;
            for (auto it = mp.begin(); it != mp.end(); ++it) {
                long long ans = it->first & nums[i]; 
                if (ans == k) {
                    count += it->second;
                }
                mp2[ans] += it->second;
            }
            mp = mp2;
        }
        return count;
    }
};
