class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, int> andMap;
        long long count = 0;

        for (int num : nums) {
            unordered_map<int, int> newAndMap;
            // andmap就是之前所有的 这样一定是连续

            if (num == k) {
                count++;
            }
            newAndMap[num]++;

            for (auto& entry : andMap) {
                int newAnd = entry.first & num;
                if (newAnd == k) {
                    count += entry.second;
                }
                newAndMap[newAnd] += entry.second;
            }

            andMap = newAndMap;
        }

        return count;
    }
};
