class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) 
    {
        int n = nums.size();
        long long count=0;
        unordered_map<long long,long long> mp1;
        for(int i=0;i<n;i++)
        {
            unordered_map<long long,long long> mp2;
            for(auto it:mp1)
            {
                long long x = it.first;
                mp2[x&nums[i]] += mp1[x];
            }
            mp2[nums[i]]++;
            count += mp2[k];
            mp1 = mp2;
        }
        return count;
    }
};
