from collections import defaultdict

class Solution(object):
    def countSubarrays(self, array, target):
        n=len(array)
        cnt=0
        prev=defaultdict(int)

        for i in range(n):
            c=defaultdict(int)
            if array[i]==target:
                cnt += 1
            c[array[i]]=1
            for v,f in prev.items():
                nr = v & array[i]
                if nr == target:
                    cnt += f
                c[nr] += f

            prev = c

        return cnt
