class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
    long long count = 0;
    unordered_map<int, int> and_map;

    for (int i = 0; i < n; ++i) {
        unordered_map<int, int> new_map;

        for (auto& [val, freq] : and_map) {
            int new_val = val & nums[i];
            new_map[new_val] += freq;
        }

        new_map[nums[i]]++;

        for (auto& [val, freq] : new_map) {
            if (val == k) {
                count += freq;
            }
        }

        and_map = new_map;
    }

    return count;
    }
};
