class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size=nums.size();
        long long ans=0;
        unordered_map<long long,long long> pre;
        for(int i=0;i<size;i++){
            unordered_map<long long,long long> cur;
            if(nums[i]==k)
                ans++;
            cur[nums[i]]=1;
            for(auto&[val,freq]:pre){
                long long newr=val&nums[i];
                if(newr==k)
                    ans+=freq;
                    cur[newr]+=freq;
            }
            pre=cur;
        }
        return ans;
    }
};
