class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
          int n = nums.size();
        long long count = 0;

        map<long long , long long> results;
        for(int i =0;i<n;++i)
        {
            map<long long , long long> current;
            if(nums[i] == k)
            {
                ++count;
            }

            current[nums[i]] =1;

        for( auto& [value, frequency]: results)
        {
            long long newresults = value & nums[i];

            if(newresults == k)
            {
                count = count + frequency;
            }
            current[newresults] += frequency;
        }

            results = current;

        }
        return count;
    }
};
