class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
          int n = nums.size();

       unordered_map<long long, long long> mp1;
       long long cnt = 0;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long>mp2; 

            if (nums[i] == k) {
                ++cnt;
            }
            mp2[nums[i]] = 1;

            for (auto& [val, freq] : mp1) {
              long long mp3 = val & nums[i];
                if (mp3 == k) {
                    cnt += freq;
                }
                mp2[mp3] += freq;
            }

            mp1 = mp2;
}
return cnt;
    }
};
