class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:

        res=0

        freqMap=defaultdict(int)

        for i in range(len(nums)):
            tempMap=defaultdict(int)
            if nums[i]==k:
                res+=1
            tempMap[nums[i]]=1

            # Iterate to map

            for num in freqMap:
                freq=freqMap[num]
                currAND=nums[i] & num
                if currAND==k:
                    res+=freq
                tempMap[currAND]+=freq
            freqMap=tempMap

        return res




