class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         int n = nums.size();
        long long count = 0;
        unordered_map<int, int> andMap;

        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> newAndMap;
            newAndMap[nums[i]]++;

            for (auto &entry : andMap) {
                int newAndValue = entry.first & nums[i];
                newAndMap[newAndValue] += entry.second;
            }

            if (newAndMap.count(k)) {
                count += newAndMap[k];
            }

            andMap = newAndMap;
        }

        return count;  
    }
};
