class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        map<long long, long long> prev;
        for (int i = 0; i < n; ++i) {
            map<long long, long long> curr; 

            if (nums[i] == k) {
                cnt++;
            }
            curr[nums[i]] = 1;
            for (auto [x, y] : prev) {
                long long ans = x & nums[i];
                if (ans == k) {
                    cnt += y;
                }
                curr[ans] += y;
            }

            prev = curr;
        }
        return cnt;
    }
};
