class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
           int n = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long> pre;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cur; 

            if (nums[i] == k) {
                ++cnt;
            }
            cur[nums[i]] = 1;

            for (auto& [v, f] : pre) {
                long long x = v & nums[i];
                if (x == k) {
                    cnt += f;
                }
                cur[x] += f;
            }

            pre = cur;
        }

        return cnt;

    }
};
