#define ll long long
#define debug(x) cout << #x << " " << x << endl;

void debug_map(const unordered_map<ll, ll>& m) {
    for (const auto& [key, value] : m) {
        cout << "Key: " << key << ", Value: " << value << endl;
    }
    cout << endl;
}

class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n = arr.size();
        ll cnt = 0;

        unordered_map<ll, ll> m;

        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> mp; 

            if (arr[i] == k)
            {
                ++cnt;
            }

            mp[arr[i]] = 1;

            for (auto &it : m) {
                ll temp = it.first & arr[i];
                if(temp == k) cnt += it.second;
                // ll temp = k1 & arr[i];
                // if (temp == k) {
                //     cnt += k2;
                // }
                mp[temp] += it.second;
            }
            // debug(m);
            m = mp;
        }

        return cnt;
    }
};
