#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
            ll n= nums.size();
            ll cnt=0;
            map<ll,ll> mp;
            for(int i=0; i<n;i++){
                map<ll,ll> mp2;
                if(nums[i]==k){
                    cnt++;
                }
                mp2[nums[i]]++;
                for(auto &v: mp){
                    ll val= v.first & nums[i];
                    if(val==k){
                        cnt+= v.second;
                    }
                    mp2[val]+= v.second;
                }
                mp=mp2;
            }
            return cnt;
    }
};
