class Solution {
public:
#define ll long long
    long long countSubarrays(vector<int>& nums, int k) {

        ll res = 0;

        int n = nums.size();

        unordered_map<ll,ll> mp,mp2;

        for(int i = 0; i < n; i++){

            mp2.clear();
            int ele = nums[i];

            if(ele == k){
                res += 1;
            }
            mp2[ele] = 1;

            for(auto [v,f] : mp){
                ll res2 = v&ele;
                if(res2 == k){
                    res += f;
                }
                mp2[res2] = mp2[res2] + f;
            }

            mp = mp2;
        }

        return res;
    }
};
