class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long totalCount = 0;

        unordered_map<long long, long long> previousResults;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currentResults;

            if (nums[i] == k) {
                ++totalCount;
            }
            currentResults[nums[i]] = 1;

            for (const auto& [value, frequency] : previousResults) {
                long long newBitwiseAnd = value & nums[i];
                if (newBitwiseAnd == k) {
                    totalCount += frequency;
                }
                currentResults[newBitwiseAnd] += frequency;
            }

            previousResults = currentResults;
        }

        return totalCount;
    }
};
