        // return cnt;

        int n = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long> prv;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr;

            if (nums[i] == k) {
                ++cnt;
            }
            curr[nums[i]] = 1;

            for (auto& [val, freq] : prv) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    cnt += freq;
                }
                curr[newAndResult] += freq;
            }

            prv = curr;
        }

        return cnt;
    }
