class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        unordered_map<int, int> freq;
        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> newf;
        for (auto& it : freq) {
            long long new_and = it.first & nums[i];
            if (new_and == k) {
                cnt += it.second;
            }
            newf[new_and] += it.second;
        }
        newf[nums[i]] += 1;
        if (nums[i] == k) {
            cnt++;
        }
        freq = newf;
    }
    return cnt;
    }
};
