class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ret = 0
        dct = {}

        for x in nums:
            new_dct = {}
            for y, ny in dct.items():
                z = x & y
                if z == k:
                    ret += ny
                new_dct[z] = new_dct.get(z, 0) + ny
            if x == k:
                ret += 1                        
            new_dct[x] = new_dct.get(x, 0) +  1
            dct = new_dct            

        return ret
