class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long res=0;
        unordered_map<long long ,long long> mp1;
        int n=nums.size();
        for(int i=0;i<n;i++)
        {
            if(nums[i]==k)
            {
                res++;
            }
            unordered_map<long long,long long> mp2;
            mp2[nums[i]]=1;
            for(auto &it:mp1)
            {
                long long temp=it.first & nums[i];
                if(temp==k)
                {
                    res+=it.second;
                }
                mp2[temp]+=it.second;
            }
            mp1=mp2;
        }
        return res;
    }
};
