class Solution {
public:
   long long countSubarrays(std::vector<int>& nums, int k) {
        int length = nums.size();
        long long count = 0;

        unordered_map<long long, long long> prev;

        for (int i = 0; i < length; ++i) {
            unordered_map<long long, long long> curr; 

            if (nums[i] == k) {
                ++count;
            }
            curr[nums[i]] = 1;

            for (auto& previousResult : prev) {
                long long value=previousResult.first;
                long long frequency=previousResult.second;
                long long temp = value & nums[i];
                if (temp == k) {
                    count += frequency;
                }
                curr[temp] += frequency;
            }

            prev = curr;
        }

        return count;
    }
};
