class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n =nums.size();
        long long count = 0;
        unordered_map<long long, long long>m;
        for(int i=0; i<n; ++i) {
            unordered_map<long long, long long> p; 
            if (nums[i]==k) ++count;
            p[nums[i]]=1;
            for(auto& v : m) {
                long long val=v.first,freq=v.second;
                long long x = val&nums[i];
                if (x==k) count+=freq;
                p[x] += freq;
            }
                m = p;
            }
            return count;
        }
};
