class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        long long int count = 0;
        unordered_map<long long int, long long int> mp;

        for (long long int i : nums) {
            unordered_map<long long int,long long int> mp2;

            if (i == k) {
                count++;
            }
            mp2[i] = 1;

            for (auto& pair : mp) {
                long long int temp = pair.first & i;
                if (temp == k) {
                    count += pair.second;
                }
                mp2[temp] += pair.second;
            }

            mp = mp2;
        }

        return count;


    }
};
