class Solution {
public:
   long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long> prevRes;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currRes; 

            if (nums[i] == k) {
                cnt++;
            }
            currRes[nums[i]] = 1;

            for (auto& [val, freq] : prevRes) {
                long long AndRes = val & nums[i];
                if (AndRes == k) {
                    cnt += freq;
                }
                currRes[AndRes] += freq;
            }

            prevRes = currRes;
        }

        return cnt;
    }
};
