class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k){
        int n=nums.size();
        long long c=0;

        unordered_map<long long, long long> pR;

        for(int i=0; i<n;i++) {
            unordered_map<long long, long long> cR; 

            if(nums[i]==k) c++;
            cR[nums[i]] = 1;

            for (auto& [val, freq]:pR) {
                long long newAndResult=val&nums[i];
                if(newAndResult==k) c+=freq;
                cR[newAndResult]+=freq;
            }
            pR=cR;
        }

        return c;
    }
};
