from typing import List

class Solution:
    def countSubarrays(self, arr: List[int], target: int) -> int:
        length = len(arr)
        totalCount = 0
        previousResults = {}

        for index in range(length):
            currentResults = {}

            if arr[index] == target:
                totalCount += 1
            currentResults[arr[index]] = 1

            for value, frequency in previousResults.items():
                newAndResult = value & arr[index]
                if newAndResult == target:
                    totalCount += frequency
                currentResults[newAndResult] = currentResults.get(newAndResult, 0) + frequency

            previousResults = currentResults

        return totalCount
