public class Solution {
    public long CountSubarrays(int[] array, int target) {
        int size = array.Length;
        long total = 0;
        Dictionary<long, long> previousResults = new Dictionary<long, long>();
        for (int index = 0; index < size; ++index) {
            Dictionary<long, long> currentResults = new Dictionary<long, long>();
            if (array[index] == target) {
                ++total;
            }
            currentResults[array[index]] = 1;
            foreach (var kvp in previousResults) {
                long newAndResult = kvp.Key & array[index];
                if (newAndResult == target) {
                    total += kvp.Value;
                }
                currentResults[newAndResult] = currentResults.ContainsKey(newAndResult)? currentResults[newAndResult] + kvp.Value : kvp.Value;
            }
            previousResults = currentResults;
        }
        return total;
    }
}
