#define ll long long
class Solution {
public:
    ll solve(ll ans, ll n, unordered_map<ll,ll>& um, vector<int>& nums, int k){
        for(ll i=0; i<n; i++){
            unordered_map<ll,ll> m; 
            m[nums[i]]=1;
            if(nums[i]==k){
                ans++;
            }
            for(auto [key,value]:um){
                ll res=key&nums[i];
                m[res]+=value;
                if (res==k){
                    ans+=value;
                }
            }
            um=m;
        }
        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        ll n=nums.size();
        ll ans=0;
        unordered_map<ll,ll> um;
        return solve(ans,n,um,nums,k);
    }
};
