class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long count = 0;       
        unordered_map<long long, long long> prev; 
        for (int i = 0; i < nums.size(); i++) { 
            unordered_map<long long, long long> curr;  
            if (nums[i] == k)
                count++;
            curr[nums[i]] = 1; 

            for (auto& [v, f] : prev) { 
                long long res = v & nums[i]; 
                if (res == k) { 
                    count += f; 
                } 
                curr[res] += f; 
            } 

            prev = curr; 
        } 

        return count; 
    }
};
