class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        int K =k;
        long long count = 0;
        unordered_map<int, int> andCount;

        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> newAndCount;
            if (nums[i] == K) count++;  // Single element subarray

            for (auto& p : andCount) {
                int newAndValue = p.first & nums[i];
                newAndCount[newAndValue] += p.second;
                if (newAndValue == K) count += p.second;
            }

            newAndCount[nums[i]]++;
            andCount = newAndCount;
        }

        return count;
    }
};
