class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long c = 0;

        unordered_map<long,long>pr;
        for(int i = 0; i < n; i++)
        {
            unordered_map<long,long> cr;

            if(nums[i] == k) ++c;
            cr[nums[i]] = 1;

            for(auto&[val,freq]:pr)
            {
                long long r = val & nums[i];
                if(r == k) c+=freq;

                cr[r] += freq;
            }
            pr = cr;
        }
        return c;

    }
};
