import java.util.HashMap;

class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long ans1 = 0;

        HashMap<Long, Long> ans = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            HashMap<Long, Long> map = new HashMap<>();

            if (nums[i] == k) {
                ++ans1;
            }
            map.put((long) nums[i], 1L);
            for (HashMap.Entry<Long, Long> entry : ans.entrySet()) {
                long val = entry.getKey();
                long count1 = entry.getValue();
                long res = val & nums[i];
                if (res == k) {
                    ans1 += count1;
                }
                map.put(res, map.getOrDefault(res, 0L) + count1);
            }
            ans = map;
        }

        return ans1;
    }
}
