class Solution {
public:
    typedef long long ll;
    long long countSubarrays(vector<int>& nums, int k) {
        ll n=nums.size();
        vector<ll>v,v2;
        for(ll i=0;i<32;i++){
            ll op=k;
            ll ez=1LL<<i;
            if((ez&op)){
               v.push_back(i);
            }
            else{
                v2.push_back(i);
            }
        }

      vector<vector<ll>>dp(n,vector<ll>(32,0LL));
        for(ll i=0;i<n;i++){
            for(ll j=0;j<32;j++){
                ll x=(1LL<<j);
                ll ew=nums[i];
                if((x&ew)){
                     if(i==0){
                          dp[i][j]=1;
                     }
                    else{
                     dp[i][j]=dp[i-1][j]+1;
                    }
                }
            }
        }
        ll ans=0;
        for(ll i=0;i<n;i++){
            ll ex=i+1;
            for(ll j=0;j<v.size();j++){
                ex=min(ex,dp[i][v[j]]);
            }

           ll eo=0;
            for(ll j=0;j<v2.size();j++){
                eo=max(eo,dp[i][v2[j]]);
            }
            ans+=max(ex-eo,0LL);



        }
        return ans;

    }
};
